        z-index: -5;
        background-color: #fefefe;
        box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
        border: 1px solid rgba(233, 234, 238, 0.6);
    }

    .our-story--middle-section::after {
        content: "";
        position: absolute;
        height: 97%;
        width: 99%;
        right: 20px;
        box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
        border: 1px solid rgba(233, 234, 238, 0.6);
        top: -15px;
        left: 40px;
        transform: rotate(2deg);
        z-index: -10;
        background-color: #fbfbfb;
